import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import 'leaflet/dist/leaflet.css';
import Markers from "./Markers";
import placesData from '../assets/data.json';
import { useLocation, useNavigate } from 'react-router-dom';
import MenuBar from "./MenuBar";


const MapView = () => {
    const { places } = placesData;
    const [state, setState] = useState({
        currenlocation: { lat: '-4.029817', lng: '-79.199492' },
        zoom: 20
    })
    const location = useLocation();
    const history = useNavigate();
    console.log("prueba");

    console.log(location);
    useEffect(() => {
        if (location.state.latitude && location.state.longitud) {
            const currenlocation = {
                lat: location.state.latitude,
                lng: location.state.longitude,
            }
            setState({...state, currenlocation})

        history.replace({pathname: '/map', state: {}})

        }

    }, [])

    return (
        <div>
            <div><MenuBar></MenuBar></div>
            <div> <MapContainer center={state.currenlocation} zoom={state.zoom}>

                console.log("En el retun"+location())
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="OpenStreetMap"
                />
                <Markers places={places} />
            </MapContainer></div>

        </div>

    );
}
export default MapView;
